document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("officials-container");function n(e,n){return`\n            <div class="card text-center">\n                <div class="card-body">\n                    <div class="avatar-placeholder" style="width: 100px; height: 100px; background: #ddd; border-radius: 50%; margin: 0 auto 1rem; display: flex; align-items: center; justify-content: center;">\n                        <i class="bi bi-person-fill" style="font-size: 3rem; color: #666;"></i>\n                    </div>\n                    <h3 class="card-title">${e.name}</h3>\n                    <p class="text-primary font-weight-bold">${e.title||n}</p>\n                </div>\n            </div>\n        `}e&&fetch("../data/officials.json").then(e=>{if(!e.ok)throw new Error("Network response was not ok");return e.json()}).then(t=>{!function(t){let r="";r+='<div class="grid grid-2 mb-5">',t.mayor&&(r+=n(t.mayor,"Mayor"));t.vice_mayor&&(r+=n(t.vice_mayor,"Vice Mayor"));r+="</div>",t.councilors&&t.councilors.length>0&&(r+='<h3 class="text-center mt-5 mb-4">Sangguniang Bayan Members</h3>',r+='<div class="grid grid-4">',t.councilors.forEach(e=>{r+=n(e,"SB Member")}),r+="</div>");e.innerHTML=r}(t)}).catch(n=>{console.error("Error fetching officials data:",n),e.innerHTML='<p class="text-center text-danger">Failed to load officials data.</p>'})});