function formatPesoMillions(r){if(null==r||isNaN(Number(r)))return"N/A";const t=Number(r);if(0===t)return"₱0.00 M";const e=t<0,o=Math.abs(t).toFixed(2).split(".");return`${e?"-":""}₱${o[0].replace(/\B(?=(\d{3})+(?!\d))/g,",")}.${o[1]} M`}function formatPeso(r){if(null==r||isNaN(Number(r)))return"N/A";const t=Number(r),e=t<0,o=Math.abs(t).toFixed(2).split(".");return`${e?"-":""}₱${o[0].replace(/\B(?=(\d{3})+(?!\d))/g,",")}.${o[1]}`}export{formatPesoMillions,formatPeso};